import React, { Fragment, useState } from 'react';
import '../asset/style/TaskManager.css';
import TaskList from './TaskList';

// Ant Design
import { Input } from 'antd';


function TaskManager() {
  const [title, setTitle] = useState([
    {taskName: "HTML"},
    {taskName: "CSS"},
    {taskName: "Javascript"}
  ]);


  const addCek = () => {
    setCek(!cek);
  }

  const addTask = () => {
    setTitle(title +1)
  }

  const addStar = () => {
    setStar(!star);
  }


  return(
    <Fragment>
      <div className="task-container">
        <div className="container-page">
          <div className="task-header">
            <p>Todos</p>
            <p>Sign Out</p>
          </div>               
          <div className="task-main">
            <div className="main-left">
              <p>Nama Anda</p>
              <p>May Day</p>
              <p>Important</p>
              <p>Completed</p>
            </div>
            <div className="main-right">
              <input className="addtask" placeholder="Basic usage" />
              <button onClick={addTask}>Tambah</button>
               <hr/>
              <div className="card-task">
                <div className="card-list">
                  <div className="card-cek">
                    <button className={cek ? "yes" : "no"} onClick={addCek}>Cek</button>                    
                  </div>
                  <div className="card-title">
                  <p>
                    {title.map(judul => (
                      <TaskList task={judul.taskName}/>
                    ))}
                  </p>
                  </div>
                  <div className="card-star">
                    <button className={star ? "yes" : "no"} onClick={addStar}>Star</button>
                  </div>
                  <div className="card-edit">

                    <button>Edit</button>
                  </div>
                  <div className="card-delete">
                    <button>Del</button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>


    </Fragment>
  )
};

export default TaskManager;
